import React, { useState } from 'react';
import { Form, Button, Alert, Spinner } from 'react-bootstrap';
import api from '../services/api';

const UCCRegistrationForm = ({ onSubmit, loading }) => {
  const [formData, setFormData] = useState({
    // Required fields from backend
    clientCode: '',                  // Mandatory, 10 chars
    firstName: '',                   // Mandatory, 70 chars
    middleName: '',                  // Optional
    lastName: '',                    // Mandatory
    taxStatus: '01',                 // Mandatory, 2 chars - Default for Individual
    gender: 'M',                     // Conditional Mandatory for Individual
    dob: '',                         // Mandatory, DD/MM/YYYY
    occupationCode: '01',            // Mandatory, 2 chars - Default for Service
    holdingNature: 'SI',             // Mandatory, 2 chars - Default for Single
    email: '',                       // Mandatory, 50 chars
    address1: '',                    // Conditional Mandatory
    city: '',                        // Mandatory, 35 chars
    state: '',                       // Mandatory, 2 chars state code
    pincode: '',                     // Mandatory, 6 chars
    country: 'India',                // Mandatory, 35 chars - Default for India

    // Optional fields with defaults
    phone: '',                       // Optional, 15 chars
    mobileNo: '',                    // Conditional Mandatory for Indian
    communicationMode: 'P',          // Mandatory - Default P=Physical
    paperlessFlag: 'Z',              // Mandatory - Default Z
    nominationOpt: 'N',              // Optional - Default N
    ckycNumber: '',                  // Conditional Mandatory if KYC type C
    aadhaarUpdated: 'Y',             // Optional - Default Y
    clientType: 'P',                 // Mandatory - Default P (Physical)

    // Additional fields from the payload
    primary_holder_pan_exempt: "N",  // Default
    primary_holder_pan: "",          // Required for tax purposes
    default_dp: "CDSL",              // Default
    cdsl_dpid: "",                   // Optional
    cdslcltid: "",                   // Optional
    account_type_1: "SB",            // Default - Savings Bank
    account_no_1: "",                // Bank account number
    ifsc_code_1: "",                 // IFSC code
    default_bank_flag_1: "Y",        // Default
    cheque_name: "",                 // Name as on cheque
    div_pay_mode: "03",              // Default
    mobile_declaration_flag: "SE",   // Default
    email_declaration_flag: "SE"     // Default
  });

  const [clientStatus, setClientStatus] = useState(null);
  const [checkingStatus, setCheckingStatus] = useState(false);
  const [statusError, setStatusError] = useState(null);
  const [registrationMode, setRegistrationMode] = useState('new'); // 'new' or 'update'

  const handleChange = (e) => {
    const { name, value } = e.target;
    const updatedValue = name === 'primary_holder_pan' ? value.toUpperCase() : value;
    setFormData(prev => ({
      ...prev,
      [name]: updatedValue
    }));

    // Clear client status when client code changes
    if (name === 'clientCode') {
      setClientStatus(null);
      setStatusError(null);
    }
  };

  const handleCheckStatus = async () => {
    if (!formData.clientCode) {
      setStatusError('Please enter a client code first');
      return;
    }

    try {
      setCheckingStatus(true);
      setStatusError(null);

      const response = await api.checkClientStatus(formData.clientCode);
      setClientStatus(response.data);

      // If client exists (active or inactive), set mode to update
      if (response.data.data) {
        setRegistrationMode('update');

        if (response.data.data.client_status === "ACTIVE") {
          setStatusError('Client is already active. Any updates will modify the existing registration.');
        } else {
          setStatusError('Client exists but is inactive. This registration will reactivate the client.');
        }
      } else {
        setRegistrationMode('new');
      }

    } catch (err) {
      console.error('Failed to check client status:', err);
      setStatusError(err.response?.data?.error || 'Failed to check client status');
      // If error is "client not found", it's a new registration
      if (err.response?.status === 404 || (err.response?.data?.error && err.response.data.error.includes('not found'))) {
        setRegistrationMode('new');
      }
    } finally {
      setCheckingStatus(false);
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    const day = date.getDate().toString().padStart(2, '0');
    const month = (date.getMonth() + 1).toString().padStart(2, '0');
    const year = date.getFullYear();
    return `${day}/${month}/${year}`;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const formattedData = {
      ...formData,
      dob: formatDate(formData.dob)
    };
    onSubmit(formattedData);
  };

  return (
    <div className="card mb-4">
      <div className="card-header bg-info text-white">
        <h4 className="mb-0">UCC Registration</h4>
      </div>
      <div className="card-body">
        <Form onSubmit={handleSubmit}>
          <div className="row">
            {/* Client Code with Status Check */}
            <div className="col-md-12 mb-3">
              <div className="d-flex align-items-end">
                <Form.Group className="flex-grow-1 me-2">
                  <Form.Label>Client Code *</Form.Label>
                  <Form.Control
                    type="text"
                    name="clientCode"
                    value={formData.clientCode}
                    onChange={handleChange}
                    maxLength={10}
                    required
                  />
                  <Form.Text className="text-muted">Max 10 characters</Form.Text>
                </Form.Group>
                <Button 
                  variant="outline-info" 
                  onClick={handleCheckStatus}
                  disabled={checkingStatus || !formData.clientCode}
                  className="mb-1"
                >
                  {checkingStatus ? (
                    <>
                      <Spinner as="span" animation="border" size="sm" role="status" aria-hidden="true" className="me-1" />
                      Checking...
                    </>
                  ) : 'Check Status'}
                </Button>
              </div>
            </div>

            {statusError && (
              <div className="col-12 mb-3">
                <Alert variant={registrationMode === 'update' ? 'warning' : 'danger'}>
                  {statusError}
                </Alert>
              </div>
            )}

            {clientStatus && !statusError && (
              <div className="col-12 mb-3">
                <Alert variant="info">
                  <strong>Client Status:</strong> {clientStatus.data.client_status || 'Unknown'}
                  {clientStatus.data.dp_status && (
                    <div><strong>DP Status:</strong> {clientStatus.data.dp_status}</div>
                  )}
                  <div className="mt-2">
                    {registrationMode === 'new' ? 
                      'This will be a new client registration.' : 
                      'This will update the existing client registration.'}
                  </div>
                  {clientStatus.simulated && (
                    <div className="mt-2 small text-muted">
                      Note: This is a simulated response for development purposes.
                    </div>
                  )}
                </Alert>
              </div>
            )}

            {/* Personal Information - Required Fields */}
            <h5 className="mb-3">Personal Information</h5>

            <div className="col-md-4 mb-3">
              <Form.Group>
                <Form.Label>PAN Number *</Form.Label>
                <Form.Control
                  type="text"
                  name="primary_holder_pan"
                  value={formData.primary_holder_pan}
                  onChange={handleChange}
                  maxLength={10}
                  pattern="[A-Z]{5}[0-9]{4}[A-Z]{1}"
                  placeholder="AAAAA1234A"
                  required
                  style={{textTransform: 'uppercase'}}
                />
                <Form.Text className="text-muted">10 character PAN number</Form.Text>
              </Form.Group>
            </div>

            <div className="col-md-4 mb-3">
              <Form.Group>
                <Form.Label>First Name *</Form.Label>
                <Form.Control
                  type="text"
                  name="firstName"
                  value={formData.firstName}
                  onChange={handleChange}
                  maxLength={70}
                  required
                />
              </Form.Group>
            </div>

            <div className="col-md-4 mb-3">
              <Form.Group>
                <Form.Label>Middle Name</Form.Label>
                <Form.Control
                  type="text"
                  name="middleName"
                  value={formData.middleName}
                  onChange={handleChange}
                  maxLength={70}
                />
              </Form.Group>
            </div>

            <div className="col-md-4 mb-3">
              <Form.Group>
                <Form.Label>Last Name *</Form.Label>
                <Form.Control
                  type="text"
                  name="lastName"
                  value={formData.lastName}
                  onChange={handleChange}
                  maxLength={70}
                  required
                />
              </Form.Group>
            </div>

            <div className="col-md-4 mb-3">
              <Form.Group>
                <Form.Label>Tax Status *</Form.Label>
                <Form.Select
                  name="taxStatus"
                  value={formData.taxStatus}
                  onChange={handleChange}
                  required
                >
                  <option value="01">Individual</option>
                  <option value="02">Company</option>
                  <option value="03">NRI</option>
                  <option value="04">HUF</option>
                  <option value="05">Trust</option>
                </Form.Select>
              </Form.Group>
            </div>

            <div className="col-md-4 mb-3">
              <Form.Group>
                <Form.Label>Gender *</Form.Label>
                <Form.Select
                  name="gender"
                  value={formData.gender}
                  onChange={handleChange}
                  required
                >
                  <option value="M">Male</option>
                  <option value="F">Female</option>
                  <option value="O">Other</option>
                </Form.Select>
              </Form.Group>
            </div>

            <div className="col-md-4 mb-3">
              <Form.Group>
                <Form.Label>Date of Birth *</Form.Label>
                <Form.Control
                  type="date"
                  name="dob"
                  value={formData.dob}
                  onChange={handleChange}
                  required
                />
              </Form.Group>
            </div>

            <div className="col-md-4 mb-3">
              <Form.Group>
                <Form.Label>Occupation *</Form.Label>
                <Form.Select
                  name="occupationCode"
                  value={formData.occupationCode}
                  onChange={handleChange}
                  required
                >
                  <option value="01">Service</option>
                  <option value="02">Business</option>
                  <option value="03">Professional</option>
                  <option value="04">Agriculture</option>
                  <option value="05">Retired</option>
                  <option value="06">Housewife</option>
                  <option value="07">Student</option>
                  <option value="08">Others</option>
                </Form.Select>
              </Form.Group>
            </div>

            <div className="col-md-4 mb-3">
              <Form.Group>
                <Form.Label>Holding Nature *</Form.Label>
                <Form.Select
                  name="holdingNature"
                  value={formData.holdingNature}
                  onChange={handleChange}
                  required
                >
                  <option value="SI">Single</option>
                  <option value="JO">Joint</option>
                  <option value="AS">Anyone or Survivor</option>
                </Form.Select>
              </Form.Group>
            </div>

            {/* Contact Information */}
            <h5 className="mb-3 mt-3">Contact Information</h5>

            <div className="col-md-6 mb-3">
              <Form.Group>
                <Form.Label>Email *</Form.Label>
                <Form.Control
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  maxLength={50}
                  required
                />
              </Form.Group>
            </div>

            <div className="col-md-6 mb-3">
              <Form.Group>
                <Form.Label>Mobile Number *</Form.Label>
                <Form.Control
                  type="tel"
                  name="mobileNo"
                  value={formData.mobileNo}
                  onChange={handleChange}
                  maxLength={10}
                  pattern="[0-9]{10}"
                  required
                />
              </Form.Group>
            </div>

            <div className="col-md-12 mb-3">
              <Form.Group>
                <Form.Label>Address *</Form.Label>
                <Form.Control
                  type="text"
                  name="address1"
                  value={formData.address1}
                  onChange={handleChange}
                  required
                />
              </Form.Group>
            </div>

            <div className="col-md-4 mb-3">
              <Form.Group>
                <Form.Label>City *</Form.Label>
                <Form.Control
                  type="text"
                  name="city"
                  value={formData.city}
                  onChange={handleChange}
                  maxLength={35}
                  required
                />
              </Form.Group>
            </div>

            <div className="col-md-4 mb-3">
              <Form.Group>
                <Form.Label>State *</Form.Label>
                <Form.Select
                  name="state"
                  value={formData.state}
                  onChange={handleChange}
                  required
                >
                  <option value="">Select State</option>
                  <option value="MH">Maharashtra</option>
                  <option value="DL">Delhi</option>
                  <option value="KA">Karnataka</option>
                  <option value="TN">Tamil Nadu</option>
                  <option value="UP">Uttar Pradesh</option>
                  <option value="GJ">Gujarat</option>
                  <option value="WB">West Bengal</option>
                  <option value="RJ">Rajasthan</option>
                  <option value="MP">Madhya Pradesh</option>
                  <option value="AP">Andhra Pradesh</option>
                  <option value="TG">Telangana</option>
                </Form.Select>
              </Form.Group>
            </div>

            <div className="col-md-4 mb-3">
              <Form.Group>
                <Form.Label>Pincode *</Form.Label>
                <Form.Control
                  type="text"
                  name="pincode"
                  value={formData.pincode}
                  onChange={handleChange}
                  maxLength={6}
                  pattern="[0-9]{6}"
                  required
                />
              </Form.Group>
            </div>

            {/* Bank Information */}
            <h5 className="mb-3 mt-3">Bank Information</h5>

            <div className="col-md-4 mb-3">
              <Form.Group>
                <Form.Label>Bank Account Number *</Form.Label>
                <Form.Control
                  type="text"
                  name="account_no_1"
                  value={formData.account_no_1}
                  onChange={handleChange}
                  required
                />
              </Form.Group>
            </div>

            <div className="col-md-4 mb-3">
              <Form.Group>
                <Form.Label>IFSC Code *</Form.Label>
                <Form.Control
                  type="text"
                  name="ifsc_code_1"
                  value={formData.ifsc_code_1}
                  onChange={handleChange}
                  pattern="^[A-Z]{4}0[A-Z0-9]{6}$"
                  required
                />
              </Form.Group>
            </div>

            <div className="col-md-4 mb-3">
              <Form.Group>
                <Form.Label>Name as on Cheque *</Form.Label>
                <Form.Control
                  type="text"
                  name="cheque_name"
                  value={formData.cheque_name}
                  onChange={handleChange}
                  required
                />
              </Form.Group>
            </div>

            {/* Additional Information */}
            <h5 className="mb-3 mt-3">Additional Information</h5>

            <div className="col-md-4 mb-3">
              <Form.Group>
                <Form.Label>CKYC Number</Form.Label>
                <Form.Control
                  type="text"
                  name="ckycNumber"
                  value={formData.ckycNumber}
                  onChange={handleChange}
                  maxLength={14}
                />
              </Form.Group>
            </div>

            <div className="col-md-4 mb-3">
              <Form.Group>
                <Form.Label>Communication Mode *</Form.Label>
                <Form.Select
                  name="communicationMode"
                  value={formData.communicationMode}
                  onChange={handleChange}
                  required
                >
                  <option value="P">Physical</option>
                  <option value="E">Email</option>
                  <option value="M">Mobile</option>
                </Form.Select>
              </Form.Group>
            </div>

            <div className="col-md-4 mb-3">
              <Form.Group>
                <Form.Label>Paperless Flag</Form.Label>
                <Form.Select
                  name="paperlessFlag"
                  value={formData.paperlessFlag}
                  onChange={handleChange}
                >
                  <option value="Z">Default</option>
                  <option value="P">Paper</option>
                  <option value="E">Paperless</option>
                </Form.Select>
              </Form.Group>
            </div>
          </div>

          <div className="d-grid gap-2">
            <Button 
              type="submit" 
              variant="primary" 
              disabled={loading}
            >
              {loading ? (
                <>
                  <Spinner as="span" animation="border" size="sm" role="status" aria-hidden="true" className="me-2" />
                  Processing...
                </>
              ) : registrationMode === 'update' ? 'Update Registration' : 'Register UCC'}
            </Button>
          </div>
        </Form>
      </div>
    </div>
  );
};

export default UCCRegistrationForm;
