import React, { useState } from 'react';
import FATCAUpload from './FATCAUpload';
import FATCAReport from './FATCAReport';
import FATCAImageUpload from './FATCAImageUpload';
import { Box, Tab, Tabs } from '@mui/material';

const FATCATabs = () => {
    const [activeTab, setActiveTab] = useState(0);
    const [loading, setLoading] = useState(false);
    const [response, setResponse] = useState(null);
    const [error, setError] = useState(null);

    const handleFATCAUpload = async (formData) => {
        try {
            setLoading(true);
            setError(null);
            setResponse(null);

            const response = await fetch('/api/fatca-upload', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    reg_details: [formData]
                }),
            });

            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.message || 'Failed to submit FATCA registration');
            }

            setResponse(data);
        } catch (err) {
            console.error('FATCA Registration failed:', err);
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    const handleImageUpload = async (formData) => {
        try {
            setLoading(true);
            setError(null);
            setResponse(null);

            const response = await fetch('/api/fatca-image-upload', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
            });

            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.message || 'Failed to upload FATCA image');
            }

            setResponse(data);
        } catch (err) {
            console.error('FATCA Image Upload failed:', err);
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    const handleGenerateReport = async (formData) => {
        try {
            setLoading(true);
            setError(null);
            setResponse(null);

            const response = await fetch('/api/fatca-report', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
            });

            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.message || 'Failed to generate FATCA report');
            }

            setResponse(data);
        } catch (err) {
            console.error('FATCA Report Generation failed:', err);
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    const handleTabChange = (event, newValue) => {
        setActiveTab(newValue);
        setError(null);
        setResponse(null);
    };

    return (
        <Box sx={{ width: '100%' }}>
            <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
                <Tabs value={activeTab} onChange={handleTabChange}>
                    <Tab label="FATCA Upload" />
                    <Tab label="FATCA Report" />
                    <Tab label="FATCA Image Upload" />
                </Tabs>
            </Box>

            {activeTab === 0 && (
                <FATCAUpload
                    onSubmit={handleFATCAUpload}
                    loading={loading}
                    error={error}
                    response={response}
                />
            )}

            {activeTab === 1 && (
                <FATCAReport
                    onSubmit={handleGenerateReport}
                    loading={loading}
                    error={error}
                    response={response}
                />
            )}

            {activeTab === 2 && (
                <FATCAImageUpload
                    onSubmit={handleImageUpload}
                    loading={loading}
                    error={error}
                    response={response}
                />
            )}
        </Box>
    );
};

export default FATCATabs;
