import React, { useState, useEffect } from 'react';
import { Button, Alert, Spinner, Card, Table, Form } from 'react-bootstrap';
import api from '../services/api';

const SchemeMasterDownload = () => {
  const [loading, setLoading] = useState(false);
  const [downloading, setDownloading] = useState(false);
  const [error, setError] = useState(null);
  const [schemeData, setSchemeData] = useState(null);
  const [filterText, setFilterText] = useState('');
  const [showRawJson, setShowRawJson] = useState(false);

  useEffect(() => {
    loadSchemeData();
  }, []);

  const loadSchemeData = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await api.getSchemeMasterContent();
      setSchemeData(response.data.data);
    } catch (err) {
      console.error('Failed to load scheme master data:', err);
      setError(err.response?.data?.error || 'Failed to load scheme master data');
    } finally {
      setLoading(false);
    }
  };

  const handleDownload = async () => {
    try {
      setDownloading(true);
      setError(null);
      
      console.log('Initiating scheme master download...');
      const response = await api.downloadSchemeMaster();
      console.log('Download API response:', response);
      
      if (response.data && (response.data.success || response.data.simulated)) {
        console.log('Download successful:', response.data);
        // Show success message
        setError(null);
        // Reload the scheme data after download
        await loadSchemeData();
      } else {
        console.error('Download response format unexpected:', response);
        setError('Unexpected response format from server');
      }
    } catch (err) {
      console.error('Failed to download scheme master:', err);
      if (err.response) {
        console.error('Error response:', err.response.data);
        setError(err.response.data?.error || err.response.data?.details || 'Failed to download scheme master');
      } else if (err.request) {
        console.error('No response received from server');
        setError('No response received from server. The server may be down or unreachable.');
      } else {
        console.error('Error setting up request:', err.message);
        setError(`Error: ${err.message}`);
      }
    } finally {
      setDownloading(false);
    }
  };

  const getFilteredSchemes = () => {
    if (!schemeData || !schemeData.schemes) {
      return [];
    }

    if (!filterText.trim()) {
      return schemeData.schemes;
    }

    const searchTerm = filterText.toLowerCase();
    return schemeData.schemes.filter(scheme => {
      // Check for matches in common scheme fields
      return (
        (scheme.SYMBOL && scheme.SYMBOL.toLowerCase().includes(searchTerm)) ||
        (scheme.NAME && scheme.NAME.toLowerCase().includes(searchTerm)) ||
        (scheme.ISIN && scheme.ISIN.toLowerCase().includes(searchTerm)) ||
        (scheme.AMC_NAME && scheme.AMC_NAME.toLowerCase().includes(searchTerm))
      );
    });
  };

  return (
    <div className="scheme-master-download">
      <Card className="mb-4">
        <Card.Header className="bg-success text-white">
          <h4 className="mb-0">Scheme Master Download</h4>
        </Card.Header>
        <Card.Body>
          <div className="d-flex justify-content-between mb-4">
            <Button 
              variant="primary" 
              onClick={handleDownload}
              disabled={downloading}
            >
              {downloading ? (
                <>
                  <Spinner as="span" animation="border" size="sm" role="status" aria-hidden="true" className="me-2" />
                  Downloading...
                </>
              ) : 'Download Latest Scheme Master'}
            </Button>
            
            <Button 
              variant="outline-secondary" 
              onClick={() => setShowRawJson(!showRawJson)}
            >
              {showRawJson ? 'Show Table View' : 'Show Raw JSON'}
            </Button>
          </div>
          
          {error && (
            <Alert variant="danger">
              {error}
            </Alert>
          )}
          
          {loading ? (
            <div className="text-center py-4">
              <Spinner animation="border" role="status">
                <span className="visually-hidden">Loading...</span>
              </Spinner>
              <p className="mt-2">Loading scheme data...</p>
            </div>
          ) : schemeData ? (
            <div className="scheme-data">
              <div className="mb-3">
                <h5>Scheme Master: {schemeData.fileName}</h5>
                <p>
                  Last Updated: {new Date(schemeData.lastUpdated).toLocaleString()} | 
                  Total Schemes: {schemeData.schemeCount}
                </p>
                
                <Form.Group className="mb-3">
                  <Form.Control 
                    type="text" 
                    placeholder="Filter schemes by name, symbol, ISIN, or AMC..." 
                    value={filterText}
                    onChange={(e) => setFilterText(e.target.value)}
                  />
                </Form.Group>
              </div>
              
              {showRawJson ? (
                <div className="raw-json">
                  <pre style={{ maxHeight: '500px', overflow: 'auto' }}>
                    {JSON.stringify(getFilteredSchemes(), null, 2)}
                  </pre>
                </div>
              ) : (
                <div className="table-responsive" style={{ maxHeight: '500px', overflow: 'auto' }}>
                  <Table striped bordered hover>
                    <thead>
                      <tr>
                        <th>Symbol</th>
                        <th>Scheme Name</th>
                        <th>ISIN</th>
                        <th>AMC</th>
                        <th>NAV</th>
                        <th>Purchase Allowed</th>
                        <th>Min. Amount</th>
                      </tr>
                    </thead>
                    <tbody>
                      {getFilteredSchemes().map((scheme, index) => (
                        <tr key={index}>
                          <td>{scheme.SYMBOL}</td>
                          <td>{scheme.NAME}</td>
                          <td>{scheme.ISIN}</td>
                          <td>{scheme.AMC_NAME}</td>
                          <td>{scheme.NAV}</td>
                          <td>{scheme.PURCHASE_ALLOWED === 'Y' ? 'Yes' : 'No'}</td>
                          <td>{scheme.MINIMUM_PURCHASE_AMOUNT}</td>
                        </tr>
                      ))}
                    </tbody>
                  </Table>
                </div>
              )}
            </div>
          ) : (
            <Alert variant="info">
              No scheme data available. Please download the latest scheme master.
            </Alert>
          )}
        </Card.Body>
      </Card>
    </div>
  );
};

export default SchemeMasterDownload;
