import React, { useState } from 'react';
import { Alert, Box, Button, Card, CardContent, TextField, Typography } from '@mui/material';

const FATCAImageUpload = ({ onSubmit, loading, error, response }) => {
    const [formData, setFormData] = useState({
        file_name: '',
        pan_no: '',
        file_data: null
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleFileChange = async (e) => {
        const file = e.target.files[0];
        if (file) {
            // Update file name
            setFormData(prev => ({
                ...prev,
                file_name: file.name
            }));

            // Convert file to base64
            const reader = new FileReader();
            reader.onload = () => {
                const base64String = reader.result.split(',')[1];
                setFormData(prev => ({
                    ...prev,
                    file_data: base64String
                }));
            };
            reader.readAsDataURL(file);
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!formData.file_data) {
            alert('Please select a file to upload');
            return;
        }
        onSubmit(formData);
    };

    return (
        <Box sx={{ p: 3 }}>
            <Card>
                <CardContent>
                    <Typography variant="h6" gutterBottom>
                        FATCA Image Upload
                    </Typography>

                    {error && (
                        <Alert severity="error" sx={{ mb: 2 }}>
                            {error}
                        </Alert>
                    )}

                    {response && response.success && (
                        <Alert severity="success" sx={{ mb: 2 }}>
                            FATCA image uploaded successfully
                        </Alert>
                    )}

                    <form onSubmit={handleSubmit}>
                        <TextField
                            fullWidth
                            label="PAN Number"
                            name="pan_no"
                            value={formData.pan_no}
                            onChange={handleChange}
                            required
                            margin="normal"
                        />

                        <Box sx={{ mt: 2, mb: 2 }}>
                            <input
                                accept="image/*,.pdf"
                                style={{ display: 'none' }}
                                id="fatca-file-upload"
                                type="file"
                                onChange={handleFileChange}
                            />
                            <label htmlFor="fatca-file-upload">
                                <Button
                                    variant="outlined"
                                    component="span"
                                    fullWidth
                                >
                                    {formData.file_name || 'Choose FATCA Document'}
                                </Button>
                            </label>
                            <Typography variant="caption" color="textSecondary">
                                Supported formats: Images (JPG, PNG) and PDF
                            </Typography>
                        </Box>

                        <Button
                            type="submit"
                            variant="contained"
                            color="primary"
                            fullWidth
                            disabled={loading}
                        >
                            {loading ? 'Uploading...' : 'Upload FATCA Document'}
                        </Button>
                    </form>
                </CardContent>
            </Card>
        </Box>
    );
};

export default FATCAImageUpload;
