import React, { useState, useEffect } from 'react';
import SchemeSelector from './SchemeSelector';
import OrderForm from './OrderForm';

export default function OrderEntry() {
  const [schemes, setSchemes] = useState([]);
  const [selectedScheme, setSelectedScheme] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchSchemes();
  }, []);

  const fetchSchemes = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:4000/api/schemes');
      const data = await response.json();
      
      if (!response.ok) {
        throw new Error(data.error || 'Failed to fetch schemes');
      }
      
      setSchemes(data);
      setError(null);
    } catch (err) {
      console.error('Failed to fetch schemes:', err);
      setError('Failed to load schemes. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const handleSchemeSelect = (scheme) => {
    setSelectedScheme(scheme);
  };

  const handleSubmitOrder = async (orderData) => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:4000/api/process-order', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          schemeCode: selectedScheme.code,
          ...orderData
        }),
      });
      
      const data = await response.json();
      
      if (!response.ok) {
        throw new Error(data.error || 'Failed to submit order');
      }
      
      alert('Order submitted successfully!');
      return data;
    } catch (error) {
      console.error('Order submission failed:', error);
      alert('Order submission failed: ' + error.message);
      throw error;
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mt-4">
      <h2>Order Entry</h2>
      <div className="row">
        <div className="col-md-5">
          <SchemeSelector 
            schemes={schemes} 
            loading={loading} 
            error={error}
            onSchemeSelect={handleSchemeSelect}
            selectedScheme={selectedScheme}
          />
        </div>
        
        <div className="col-md-7">
          {selectedScheme ? (
            <OrderForm 
              scheme={selectedScheme}
              onSubmit={handleSubmitOrder}
              loading={loading}
            />
          ) : (
            <div className="card">
              <div className="card-body">
                <p className="text-center text-muted">Please select a scheme to continue</p>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
