import React, { useState } from 'react';
import { Form, Button, Alert, Spinner } from 'react-bootstrap';
import api from '../services/api';

const OrderForm = ({ scheme, onSubmit, loading }) => {
  const [formData, setFormData] = useState({
    clientCode: '',
    amount: scheme?.minAmount || '1000',
    remarks: '',
    email: '',
    mobileNo: ''
  });
  
  const [clientStatus, setClientStatus] = useState(null);
  const [checkingStatus, setCheckingStatus] = useState(false);
  const [statusError, setStatusError] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // Clear client status when client code changes
    if (name === 'clientCode') {
      setClientStatus(null);
      setStatusError(null);
    }
  };

  const handleCheckStatus = async () => {
    if (!formData.clientCode) {
      setStatusError('Please enter a client code first');
      return;
    }
    
    try {
      setCheckingStatus(true);
      setStatusError(null);
      
      const response = await api.checkClientStatus(formData.clientCode);
      setClientStatus(response.data);
      
      // If simulated response or actual response shows inactive status
      if ((response.data.simulated && response.data.data.client_status === "INACTIVE") || 
          (response.data.data && response.data.data.client_status === "INACTIVE")) {
        setStatusError('Client is inactive. Please complete UCC registration first.');
      }
      
    } catch (err) {
      console.error('Failed to check client status:', err);
      setStatusError(err.response?.data?.error || 'Failed to check client status');
    } finally {
      setCheckingStatus(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
  };

  return (
    <div className="card">
      <div className="card-header bg-success text-white">
        <h4 className="mb-0">Order Entry Form</h4>
      </div>
      <div className="card-body">
        <div className="mb-3">
          <h5>Selected Scheme:</h5>
          <p className="mb-1"><strong>Code:</strong> {scheme.code}</p>
          <p className="mb-1"><strong>Name:</strong> {scheme.name}</p>
          <p className="mb-0"><strong>Min Amount:</strong> ₹{scheme.minAmount}</p>
        </div>

        <Form onSubmit={handleSubmit}>
          <Form.Group className="mb-3">
            <Form.Label>Client Code *</Form.Label>
            <div className="d-flex">
              <Form.Control
                type="text"
                name="clientCode"
                value={formData.clientCode}
                onChange={handleChange}
                required
                maxLength={10}
              />
              <Button 
                variant="outline-info" 
                className="ms-2" 
                onClick={handleCheckStatus}
                disabled={checkingStatus || !formData.clientCode}
              >
                {checkingStatus ? (
                  <>
                    <Spinner as="span" animation="border" size="sm" role="status" aria-hidden="true" className="me-1" />
                    Checking...
                  </>
                ) : 'Check Status'}
              </Button>
            </div>
          </Form.Group>

          {statusError && (
            <Alert variant="danger" className="mb-3">
              {statusError}
            </Alert>
          )}

          {clientStatus && !statusError && (
            <Alert variant="success" className="mb-3">
              Client status: {clientStatus.data.client_status || 'ACTIVE'}
              {clientStatus.data.dp_status && (
                <div>DP status: {clientStatus.data.dp_status}</div>
              )}
              {clientStatus.simulated && (
                <div className="mt-2 small text-muted">
                  Note: This is a simulated response for development purposes.
                </div>
              )}
            </Alert>
          )}

          <Form.Group className="mb-3">
            <Form.Label>Amount (₹) *</Form.Label>
            <Form.Control
              type="number"
              name="amount"
              value={formData.amount}
              onChange={handleChange}
              min={scheme.minAmount || 1000}
              step="1"
              required
            />
            <Form.Text className="text-muted">
              Minimum amount: ₹{scheme.minAmount || 1000}
            </Form.Text>
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Email</Form.Label>
            <Form.Control
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
            />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Mobile Number</Form.Label>
            <Form.Control
              type="tel"
              name="mobileNo"
              value={formData.mobileNo}
              onChange={handleChange}
              pattern="[0-9]{10}"
            />
            <Form.Text className="text-muted">
              10-digit mobile number
            </Form.Text>
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Remarks</Form.Label>
            <Form.Control
              as="textarea"
              name="remarks"
              value={formData.remarks}
              onChange={handleChange}
              rows={2}
            />
          </Form.Group>

          <Button 
            type="submit" 
            variant="primary" 
            className="w-100" 
            disabled={loading || statusError}
          >
            {loading ? (
              <>
                <Spinner as="span" animation="border" size="sm" role="status" aria-hidden="true" className="me-2" />
                Processing...
              </>
            ) : 'Submit Order'}
          </Button>
        </Form>
      </div>
    </div>
  );
};

export default OrderForm;
