import React, { useState, useEffect } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import SchemeSelector from './components/SchemeSelector';
import OrderForm from './components/OrderForm';
import ResponseDisplay from './components/ResponseDisplay';
import api from './services/api';

function App() {
  const [schemes, setSchemes] = useState([]);
  const [selectedScheme, setSelectedScheme] = useState(null);
  const [loading, setLoading] = useState(false);
  const [response, setResponse] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchSchemes = async () => {
      try {
        setLoading(true);
        const response = await api.getSchemes();
        setSchemes(response.data);
        setError(null);
      } catch (err) {
        console.error('Failed to fetch schemes:', err);
        setError('Failed to load schemes. Please try again later.');
        toast.error('Failed to load schemes');
      } finally {
        setLoading(false);
      }
    };

    fetchSchemes();
  }, []);

  const handleSchemeSelect = (scheme) => {
    setSelectedScheme(scheme);
    setResponse(null);
  };

  const handleSubmitOrder = async (orderData) => {
    try {
      setLoading(true);
      setError(null);
      setResponse(null);
      
      const response = await api.submitOrder({
        schemeCode: selectedScheme.code,
        ...orderData
      });
      
      setResponse(response.data);
      toast.success('Order submitted successfully');
    } catch (err) {
      console.error('Order submission failed:', err);
      setError(err.response?.data?.error || 'Failed to submit order');
      toast.error('Order submission failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mt-4">
      <ToastContainer position="top-right" autoClose={3000} />
      
      <div className="row mb-4">
        <div className="col-12">
          <div className="card">
            <div className="card-header bg-primary text-white">
              <h2 className="mb-0">NSE Invest Order Entry</h2>
            </div>
            <div className="card-body">
              <p className="lead">
                Select a scheme from the list below and submit your order details.
              </p>
            </div>
          </div>
        </div>
      </div>

      <div className="row">
        <div className="col-md-5">
          <SchemeSelector 
            schemes={schemes} 
            loading={loading} 
            error={error}
            onSchemeSelect={handleSchemeSelect}
            selectedScheme={selectedScheme}
          />
        </div>
        
        <div className="col-md-7">
          {selectedScheme ? (
            <OrderForm 
              scheme={selectedScheme}
              onSubmit={handleSubmitOrder}
              loading={loading}
            />
          ) : (
            <div className="card">
              <div className="card-body">
                <p className="text-center text-muted">Please select a scheme to continue</p>
              </div>
            </div>
          )}
          
          {response && (
            <ResponseDisplay response={response} />
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
